package smel.pan.j8.Pack1;import java.util.function.BinaryOperator;import java.util.function.Function;/** * Created by SmelayaPanda on 20.04.2016. * summ test */public class FunctionSumm {    public static void main(String[] args) {        Function<Integer, Function<Integer, Integer>> add = x -> y -> (x + y);        Integer summ = add.apply(10).apply(15);        System.out.println(summ);        /**         * more smart way         * */        BinaryOperator<Integer> sum = (Integer c, Integer d) -> c + d;        Integer res = sum.apply(2, 4);        System.out.println(res);    }}