package smel.pan.j8.Pack2;import java.util.function.Consumer;/** * Created by SmelayaPanda on 20.04.2016. * Consumers represents operations to be performed on a single input argument. */public class ConsumerTest {    public static void main(String[] args) {        Consumer<Person> greeter = person2 ->                System.out.println("Hello " + person2.name);        greeter.accept(new Person("Lisa", 21));    }}