package smel.pan.pack3_stream;import java.util.ArrayList;import java.util.List;/** * Created by SmelayaPanda on 21.04.2016. * * @MapOperation The intermediate operation map converts each element into another object via the given function. * The following example converts each string into an upper-cased string. * But you can also use map to transform each object into another type. * The generic type of the resulting stream depends on the generic type of the function you pass to map */public class MapOperationTest {    public static void main(String[] args) {        List<String> strList = new ArrayList<>();        strList.add("Apple");        strList.add("Mac");        strList.add("IBM");        strList.add("Microsoft");        strList.add("Oracle");        strList.add("Sun");        strList                .stream()                .map(String::toUpperCase)                .sorted((o1, o2) -> o1.compareTo(o2))                .forEach(System.out::println);    }}